mapper.select:
select * from water_quality


        and issue = #{issue}


        AND status=#{status}


        AND createUser like CONCAT('%',#{createUser},'%')


        and status !=#{num}

select sm_user.ID from sm_user INNER JOIN
(select sur.USER_ID from sm_role sr INNER JOIN sm_user_role sur on sr.id=sur.ROLE_ID where sr.name='市河长办'
)s on sm_user.id =s.USER_ID

select sm_user.ID from sm_user INNER JOIN
(select sur.USER_ID from sm_role sr INNER JOIN sm_user_role sur on sr.id=sur.ROLE_ID where sr.name='水文水资源中心'
)s on sm_user.id =s.USER_ID
