mapper.select:
select * from surface_water


        and issue = #{issue}


        AND status=#{status}


        AND createUser like CONCAT('%',#{createUser},'%')


        and status !=#{num}

select sm_user.ID from sm_user INNER JOIN
(select sur.USER_ID from sm_role sr INNER JOIN sm_user_role sur on sr.id=sur.ROLE_ID where sr.id='fca74e7d677111e7968ef01fafcf3a37'
)s on sm_user.id =s.USER_ID where sm_user.region_id='120100000000';

